import React from "react";
import { Pannellum } from "pannellum-react";
import G02 from "../../../media/photos360/G02.jpg";
import G07 from "../../../media/photos360/G07.jpg";
import G04 from "../../../media/photos360/G04.jpg";


function G2(){

  let x=document.getElementsByClassName("G02");
  x.style.display="block";

  let y=document.getElementsByClassName("G04");
  y.style.display="none";

  let z=document.getElementsByClassName("G07");
  z.style.display="none";
}

function G4(){

  let  x=document.getElementsByClassName("G04");
  x.style.display="block";

  let  y=document.getElementsByClassName("G02");
  y.style.display="none";

  let z=document.getElementsByClassName("G07");
  z.style.display="none";
}

function G7(){

  let  x=document.getElementsByClassName("G07");
  x.style.display="block";

  let  y=document.getElementsByClassName("G04");
  y.style.display="none";

  let z=document.getElementsByClassName("G02");
  z.style.display="none";
}

const Photos360 = () => {
  return (
    <div className="photos360">
      <h2>LES PHOTOS 360 DE L'IUT</h2>
      <h3 className="text360">
        {" "}
        Quelques photos en 360° qui ont été prise lors de TP de première année à
        l'IUT
      </h3>
      <p>
        Pour les afficher, cliquez sur "Load panorama". Vous pouvez avoir
        certaines informations en cliquant sur les{" "}
        <i class="fas fa-info-circle" /> présents dans les images
      </p>

    <div className="bouton360">
    <button type="button" onclick={G2()}>G02</button>
    <button type="button" onclick={G4()}>G04</button>
    <button type="button" onclick={G7()}>G07</button>
    </div>
      <div className="div360">
        <div className="G07">
          <Pannellum
            width="90%"
            height="500px"
            image={G07}
            pitch={10}
            yaw={180}
            hfov={110}
            previewTitle="Salle G07"
            title="Salle G07"
            onLoad={() => {
              console.log("panorama loaded");
            }}
          >
            <Pannellum.Hotspot
              type="info"
              pitch={10}
              yaw={-10}
              text="Baie de brassage qui permet de cabler les différents équipements"
            />

            <Pannellum.Hotspot
              type="info"
              pitch={-30}
              yaw={110}
              text="Maquette d'un TP de câblage de première année"
            />

            <Pannellum.Hotspot
              type="info"
              pitch={-20}
              yaw={-120}
              text="TP de téléphonie de première année"
            />
          </Pannellum>
        </div>

        <div className="G04">
          <Pannellum
            width="90%"
            height="500px"
            image={G04}
            pitch={10}
            yaw={180}
            hfov={110}
            previewTitle="Salle G04"
            title="Salle G04"
            onLoad={() => {
              console.log("panorama loaded");
            }}
          >
            <Pannellum.Hotspot
              type="info"
              pitch={-20}
              yaw={-150}
              text="TP de signal de première année"
            />
          </Pannellum>
        </div>

        <div className="G02">
          <Pannellum
            width="90%"
            height="500px"
            image={G02}
            pitch={10}
            yaw={180}
            hfov={110}
            previewTitle="Salle G02"
            title="Salle G02"
            onLoad={() => {
              console.log("panorama loaded");
            }}
          >
            <Pannellum.Hotspot
              type="info"
              pitch={-10}
              yaw={110}
              text="Osciloscope et générateur de signal utilisé lors des TP d'électronique ou de signal"
            />

            <Pannellum.Hotspot
              type="info"
              pitch={-20}
              yaw={-120}
              text="TP de téléphonie de première année"
            />
          </Pannellum>
        </div>
      </div>
    </div>
  );
};

export default Photos360;
